mapper.select:
SELECT

FROM task_finish_details


    uid = #{uid}


    and task_key = #{taskKey}


    and finish_time = #{finishTime}


    and addition_id = #{additionId}

select

from task_finish_details


    uid = #{uid}


    and status = #{status}


    and task_key = #{taskKey}


    and finish_time = #{finishTime}
